var FamilyDashboardTable = (function (FamilyDashboardTable) {
  var tools = FamilyDashboardTable.tools = FamilyDashboardTable.tools || {};
  tools.Editor = Class.create({
    initialize : function (element) {
      this.element = element;

      if (this.element.hasClassName("aggregateFeature")) {
        this.featureLine = this.element.down('.feature-text');
        this.featureLine.observe("mouseover", this._handleMouseOverFeature.bind(this));
        this.featureLine.observe("mouseout", this._handleMouseOutFeature.bind(this)); 
      }
      this.element.__self = this;
    },
    _handleMouseOverFeature: function() {
      var memberCasesEl = this.featureLine.down('.memberCases');
      var memberIds = memberCasesEl.value.split(' ');
      memberIds.each(function(memberId) {
        if (memberId) {
          $(document.documentElement).down('table[id=family-members-table]').select('.family-member-row').each(function(row) {
            if (row.down('.identifier').innerHTML == memberId) {
              row.setAttribute('style', "background: #e6f4fa")
            }
          })
        }
      })
    },
    _handleMouseOutFeature: function() {
      var memberCasesEl = this.featureLine.down('.memberCases');
      var memberIds = memberCasesEl.value.split(' ');
      memberIds.each(function(memberId) {
        if (memberId) {
          $(document.documentElement).down('table[id=family-members-table]').select('.family-member-row').each(function(row) {
            if (row.down('.identifier').innerHTML == memberId) {
              row.setAttribute('style', "background: #ffffff")
            }
          })
        }
      })
    }
  })

  var init = function(event) {
    ((event && event.memo.elements) || [$('body')]).each(function(element) {
      element.select('.aggregateFeature').each(function(item) {
        if (!item.__self) {
          item.__self = new FamilyDashboardTable.tools.Editor(item);
        }
      });
    });
    return true;
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
  document.observe("xwiki:dom:updated", init);

  return FamilyDashboardTable;
}(FamilyDashboardTable || {}));

var AnalysisStatus = (function (AnalysisStatus) {
  var tools = AnalysisStatus.tools = AnalysisStatus.tools || {};
  tools.Editor = Class.create({
    initialize : function (element) {
      this.element = element;

      if (this.element.hasClassName('displayed-value')) {
        this._checkboxDisplayedValue = this.element; 
        if (this._checkboxDisplayedValue.up('.fieldset.isActiveUser').hasClassName('userActiveOnFamily')) {
          this._checkboxDisplayedValue.innerHTML = "Yes";
        } else {
          this._checkboxDisplayedValue.innerHTML = "No";
        } 
      } else {
        this._analysisStatusCheckbox = this.element;
        if (this._analysisStatusCheckbox.up('.fieldset.isActiveUser').hasClassName('userActiveOnFamily')) {
          this._analysisStatusCheckbox.checked = true;
        } else {
          this._analysisStatusCheckbox.checked = false; 
        }
        this._currentUser = $(document.documentElement).down('.currentUser').value;
        this._currentUserName = $(document.documentElement).down('.currentUserName').down('a').innerHTML; 
        this._acceptedSuggestions = $(document.documentElement).down('.users .accepted-suggestions');
        this._usersHeader = $(document.documentElement).down('.users label.section');

        this._currentUserListEl = "";
        this._analysisStatusCheckbox.observe("click", this._handleAnalysisCheckboxClick.bind(this));
      }

      this.element.__self = this;
    },
    _isCurrentUserListed: function() {
      var validated = false; 
      var userInputs = this._acceptedSuggestions.select('input');
      for (var i = 0; i < userInputs.length; i++) {
        if (userInputs[i].value == this._currentUser) {
          validated = true;
          this._currentUserListEl = userInputs[i].up('li');
          break;
        }
      }
      return validated;
    },
    _addUserElement: function() {
      if (this._currentUser && this._acceptedSuggestions) {
        var noAvatarImg = 'http://localhost:8080/skin/resources/icons/xwiki/noavatar.png';
        var deleteMsg = 'Remove this item from the list of selected items';
        var name = 'PhenoTips.FamilyMetaClass_0_users';

        var userItem = new Element('li');
        var userEl = new Element('div', {'class': 'user'}); 
        var userAvatarWrapperEl = new Element('div', {'class': 'user-avatar-wrapper'})
          .insert(new Element('img', {'src': noAvatarImg, 'class': 'icon'}));
        var userNameEl = new Element('div', {'class': 'user-name'}).insert(this._currentUserName);
        var userAlias = new Element('div').insert(new Element('span', {'class': 'user-alias'}).insert(this._currentUser.substring(6)));
        var valueBinder = new Element('input', {'type': 'hidden', 'name': name, 'value': this._currentUser});

        userEl
          .insert(userAvatarWrapperEl)
          .insert(userNameEl)
          .insert(userAlias);

        userItem
          .insert(userEl)
          .insert(valueBinder);

        this._acceptedSuggestions.insert(userItem);
      }
    },
    _removeUserElement: function() {
      this._acceptedSuggestions.removeChild(this._currentUserListEl); 
    },
    _handleAnalysisCheckboxClick: function() {
      var isCurrentUserListed = this._isCurrentUserListed(); 
      if (this._analysisStatusCheckbox.checked && !isCurrentUserListed) {
        this._addUserElement();
      } else if (!this._analysisStatusCheckbox.checked && isCurrentUserListed) {
        this._removeUserElement();
      }
      this._hideActiveUsersHeader(); 
    },
    _hideActiveUsersHeader: function() {
      if (this._acceptedSuggestions.select('li').length == 0) {
        this._usersHeader.style.display = "none";
      } else {
        this._usersHeader.style.display = "block";
      }
    }
  });

  var init = function(event) {
    ((event && event.memo.elements) || [$('body')]).each(function(element) {
      element.select('input[id$="isActiveUser"][type=checkbox]').each(function(item) {
        if (!item.__self) {
          item.__self = new AnalysisStatus.tools.Editor(item);
        }
      });
      element.select('.fieldset.isActiveUser span.displayed-value').each(function(item) {
        if (!item.__self) {
          item.__self = new AnalysisStatus.tools.Editor(item);
        }
      });
    });
    return true;
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
  document.observe("xwiki:dom:updated", init);

  return AnalysisStatus;
}(AnalysisStatus || {}));

var PhenoTips = (function (PhenoTips) {
  var tools = PhenoTips.tools = PhenoTips.tools || {};

  tools.FamilyFromFamilyGroupDeleter = Class.create({
    initialize : function (element, source) {
      this.element = element;
      this._source = source;
      this._xWikiSpace = "/families";
      this._tableSection = $(document.documentElement).down('.table-with-autosuggest-adder');
      this._deleteButton = this.element;
      if (this._deleteButton) {
        this._handleDelete = this._ajaxDeleteFamilyFromFamilyGroup.bind(this);
        this._deleteButton.observe("click", this._handleDelete);
      }
      this.element.__self = this;
    },
    _ajaxDeleteFamilyFromFamilyGroup : function(e) {
      e.stop();
      var deleteTrigger = e.element();
      var _tableSection = this._tableSection;
      var _source = this._source;

      var groupId, familyId;
      if (_source == 'family-dashboard') {
        groupId = deleteTrigger.up('tr[class$="-row"]').id; 
        familyId = this._tableSection.down('.row-adder').id;
      } else if (_source == 'family-groups-dashboard') {
        groupId = this._tableSection.down('.row-adder').id;
        familyId = deleteTrigger.up('tr[class$="-row"]').id;
      }
      var resourceURI = XWiki.contextPath + "/rest/family-groups/" + groupId + this._xWikiSpace + "/" + familyId;

      new XWiki.widgets.ConfirmationBox(
        {
          onYes: function() {
            deleteTrigger._x_notif = new XWiki.widgets.Notification('Deleting...', 'inprogress');
            var request = new XMLHttpRequest();
            request.addEventListener("load", responseHandler);
            request.open("DELETE", resourceURI);
            request.send();
          },
          onNo: function(){},
        },
        {
          confirmationText: "Are you sure you want to remove the family from this family group?",
          showCancelButton: false
        }
      ).dialog.style.zIndex = 200000;

      function responseHandler () {
        if (this.response) {
          deleteTrigger.disabled = true;
          deleteTrigger._x_notif.hide();
          var rowEl = deleteTrigger.up('tr[class$="-row"]');
          rowEl.remove();
          Event.fire(document, "xwiki:dom:updated", {'elements' : [_tableSection]});
          if (_source == 'family-groups-dashboard') {
            PhenoTips.widgets.FormUtils.getFormState().saveIfFormDirty(function () { window.location.reload(true); });
          }
        } else {
          var failureReason = this.response.statusText;
          if (failureReason == '' || this.response.status == 12031) {
            failureReason = 'Server not responding'; 
          }
          if (deleteTrigger._x_notif) {
            deleteTrigger._x_notif.replace(new XWiki.widgets.Notification("Failed: " + failureReason, "error"));
          } else {
            new XWiki.widgets.Notification(this.interactionParameters.failureMessageText + failureReason, "error");
          }
        }
        deleteTrigger.disabled = false;
      }
    }
  })

  var init = function(event) {
    ((event && event.memo.elements) || [$('body')]).each(function(element) {
      element.select('.family-group-row .action-delete').each(function(item) {
        if (!item.__self) {
          item.__self = new PhenoTips.tools.FamilyFromFamilyGroupDeleter(item, 'family-dashboard');
        }
      });
    });
    return true;
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
  document.observe("xwiki:dom:updated", init);

  return PhenoTips;
}(PhenoTips || {}));

var PhenoTips = (function (PhenoTips) {
  var tools = PhenoTips.tools = PhenoTips.tools || {};

  tools.FamilyToFamilyGroupAdder = Class.create({
    initialize : function (element, source) {
      this.element = element;
      this._source = source;
      this._xWikiSpace = "/families";
      this._tableSection = $(document.documentElement).down('.table-with-autosuggest-adder');
      this._addButton = this.element.down('.action-add');
      this._handleAdd = this._ajaxAddFamilyToFamilyGroup.bind(this);
      this._addButton.observe("click", this._handleAdd);
      this.element.__self = this;
    },
    _ajaxAddFamilyToFamilyGroup : function(e) {
      e.stop();
      var addTrigger = e.element();
      var _tableSection = this._tableSection;
      var _source = this._source;

      var familyId, groupId, targetId;
      if (_source == 'family-groups-dashboard') {
        familyId = addTrigger.up('.row-adder').down('input').value;
        groupId = addTrigger.up('.row-adder').id;
        targetId = familyId;
      } else if (_source == 'family-dashboard') {
        familyId = addTrigger.up('.row-adder').id;
        groupId = addTrigger.up('.row-adder').down('input').value;
        targetId = groupId;
      }

      if (!targetId) {
        addTrigger._x_notif = new XWiki.widgets.Notification('Please enter a value', 'plain', {'timeout' : 2});
        return;
      }

      if (_tableSection.select('div[id="' + targetId + '"], tr[id="' + targetId + '"]').length > 0) {
        addTrigger._x_notif = new XWiki.widgets.Notification('The family is already part of Family Group ' + groupId, 'plain', {'timeout' : 2});
        return; 
      }

      var resourceURI = XWiki.contextPath + "/rest/family-groups/" + groupId + this._xWikiSpace;

      var postObject = {};
      postObject["familyId"] = familyId;
      var headers = {};
      headers["Content-type"] = "application/json";

      new Ajax.Request(resourceURI, {
        method: 'POST',
        requestHeaders: headers,
        parameters: JSON.stringify(postObject),
        onCreate : function() {
          addTrigger.disabled = true;
          addTrigger._x_notif = new XWiki.widgets.Notification('Adding...', 'inprogress');
        },
        onSuccess : function(response) {
          addTrigger._x_notif.hide();
          new Ajax.Request(addTrigger.href + targetId, {
            onSuccess : function(response) {

              // Temporary solution for removing extra added <p> and <br> tags
              var newRowHTMLString = response.responseText.substring(response.responseText.indexOf("<tr"));
              var newRow = new Element('tbody').update(newRowHTMLString).childNodes[0];

              if (newRow.hasClassName('withPedigrees')) {
                var pedigreesSection = newRow.down('.fg-pedigrees')
                _tableSection.insert(newRow);
                Event.fire(document, "xwiki:dom:loaded", {'elements' : [pedigreesSection]});
              } else {
                _tableSection.down('tbody').insert(newRow);
              }

              Event.fire(document, "xwiki:dom:updated", {'elements' : [_tableSection]});

              if (_source == 'family-groups-dashboard') {
                PhenoTips.widgets.FormUtils.getFormState().saveIfFormDirty(function () { window.location.reload(true); });
              }
            }
          })
        },
        onFailure : function(response) {
          var failureReason = response.statusText;

          if (failureReason == '' || response.status == 12031) {
            failureReason = 'Server not responding'; 
          }

          if (addTrigger._x_notif) {
            addTrigger._x_notif.replace(new XWiki.widgets.Notification("Failed: " + failureReason, "error", {"timeout" : 2}));
          } else {
            new XWiki.widgets.Notification(this.interactionParameters.failureMessageText + failureReason, "error");
          }
        },
        onComplete : function() {
          addTrigger.disabled = false;
        }
      })
    }
  })

  var init = function(event) {
    ((event && event.memo.elements) || [$('body')]).each(function(element) {
      element.select('.family-groups-table-section .row-adder').each(function(item) {
        if (!item.__self) {
          item.__self = new PhenoTips.tools.FamilyToFamilyGroupAdder(item, 'family-dashboard');
        }
      });
    });
    return true;
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
  document.observe("xwiki:dom:updated", init);

  return PhenoTips;
}(PhenoTips || {}));

var PhenoTips = (function (PhenoTips) {
  var tools = PhenoTips.tools = PhenoTips.tools || {};

  tools.FamilyGroupSelector = Class.create({
    initialize : function (element) {
      this.element = element;

      var _this = this;

      this.searchInput = this.element; 

      this.familyGroupSearchUrl = XWiki.contextPath + "/rest/family-groups?";

      this.searchInput._suggest = new PhenoTips.widgets.Suggest(this.searchInput, {
        script: this.familyGroupSearchUrl,
        varname: "input",
        noresults: "No matching terms",
        resultsParameter: "matchedFamilyGroups",
        json: true,
        displayId: true,
        resultId: "name",
        resultValue: "id",
        enableHierarchy: false,
        fadeOnClear: false,
        timeout: 30000,
        parentContainer: $$('.family-groups-section')[0]
      });

      this.searchInput.addClassName('initialized');
      this._onClickOutsideSuggest = this._onClickOutsideSuggest.bindAsEventListener(this);
      this.element.__self = this;
    },
    _onClickOutsideSuggest: function (event) {
      if (!event.findElement('.suggestItems')) {
          this._hideSuggestPicker();
      }
    },
    _hideSuggestPicker: function() {
      this.searchInput._suggest.clearSuggestions();
    }
  })

  var init = function(event) {
    ((event && event.memo.elements) || [$('body')]).each(function(element) {
      element.select('.familyGroup-search-input').each(function(item) {
        if (!item.__self) {
          item.__self = new PhenoTips.tools.FamilyGroupSelector(item);
        }
      });
    });
    return true;
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
  document.observe("xwiki:dom:updated", init);

  return PhenoTips;
}(PhenoTips || {}));

var PhenoTips = (function (PhenoTips) {
  var tools = PhenoTips.tools = PhenoTips.tools || {};

  tools.FamilyGroupPedigreesExporter = Class.create({
    initialize : function (element) {
      this.element = element;
      this._xWikiSpace = "/families";
      this._familyGroupsSection = $(document.documentElement).down('.family-groups-table-section');
      this._exportButton = this.element.down('.action-download');
      if (this._exportButton) {
        this._handleExport = this._ajaxExportFamilyGroupPedigrees.bind(this); 
        this._exportButton.observe("click", this._handleExport);
      }
      this.element.__self = this;
    },
    _ajaxExportFamilyGroupPedigrees : function(e) {
      e.preventDefault();
      exportTrigger = e.element();
      groupId = exportTrigger.up('.family-group-row').id;
      var resourceURI = XWiki.contextPath + "/rest/family-groups/" + groupId + "/pedigree/ped";

      new Ajax.Request(resourceURI, {
        method: 'GET',
        onCreate : function() {
          exportTrigger.disabled = true;
          exportTrigger._x_notif = new XWiki.widgets.Notification('Loading', 'inprogress');
        },
        onSuccess : function(response) {
          exportTrigger._x_notif.hide();
          window.open(resourceURI, "_blank");
        },
        onFailure : function(response) {
          var failureReason = response.statusText;
          if (failureReason == '' || response.status == 12031) {
            failureReason = 'Server not responding';
          }
          if (exportTrigger._x_notif) {
            exportTrigger._x_notif.replace(new XWiki.widgets.Notification("Failed: " + failureReason, "error"));
          } else {
            new XWiki.widgets.Notification(this.interactionParameters.failureMessageText + failureReason, "error");
          }
        },
        onComplete : function() {
          exportTrigger.disabled = false;
        }
      }); 
    }
  })

  var init = function(event) {
    ((event && event.memo.elements) || [$('body')]).each(function(element) {
      element.select('.family-group-row').each(function(item) {
        if (!item.__self) {
          item.__self = new PhenoTips.tools.FamilyGroupPedigreesExporter(item);
        }
      });
    });
    return true;
  };

  (XWiki.domIsLoaded && init()) || document.observe("xwiki:dom:loaded", init);
  document.observe("xwiki:dom:updated", init);

  return PhenoTips;
}(PhenoTips || {}));

