document.observe('xwiki:dom:loaded', function ()
{
  /*
  This is a hack. It shall be replaced when the family page becomes modular.
   */
  var pedigreeClass = 'pedigree-family-section';
  var pedigreeSection = $$('.' + pedigreeClass);
  if (pedigreeSection.length > 0) {
    var originalHeader = $(pedigreeSection[0]).down('.wikigeneratedheader');
    var classes = originalHeader.readAttribute("class");
    var id = originalHeader.readAttribute("id");
    var replacement = new Element('h2', {'id': id, 'class': classes});
    originalHeader.childElements().each(function (elem)
    {
      replacement.insert({bottom: elem});
    });
    originalHeader.insert({
      before: replacement
    });
    originalHeader.remove();
  }
});

require(['jquery'], function ($)
{
  $(document).ready(function ()
  {
    var patientListHolder = $("#members").find(".fieldset");
    var reportsHolder = $('#member-medical-reports');
    var reportsSection = reportsHolder.parents("#reports");
    var familyJson = $.parseJSON($('#family-info-json').html());
    var patientJson = familyJson.familyMembers;

    var patientListDisplay = "";
    var reportsDisplay = "";
    for (var i = 0; patientJson.length > i; i++) {
      var reports = patientJson[i].reports;
      patientListDisplay += displayPatientLink(patientJson[i]);
      if (!$.isEmptyObject(reports)) {
        reportsDisplay += displayPatientTitle(patientJson[i]);
        reportsDisplay += displayMedicalReports(reports);
      }
    }

    patientListHolder.html(patientListDisplay);
    reportsHolder.html(reportsDisplay);
    if (reportsDisplay) {
      reportsSection.show();
    }

    /** Displays patient information as a link. */
    function displayPatientLink(patientJson)
    {
      return "<p><a href='" + patientJson.url + "' target='_blank'>" + displayPatient(patientJson) + "</a></p>";
    }

    /** Shows formatted patient's id, external id and name as a heading */
    function displayPatientTitle(patientJson)
    {
      return "<h3>" + displayPatient(patientJson) + "</h3>";
    }

    function displayPatient(patientJson) {
      var display = patientJson.id;

      var closeBracket = false;
      if (patientJson.identifier || patientJson.name) {
        display += " (";
        closeBracket = true;
      }
      if (patientJson.identifier) {
        display += patientJson.identifier;
        if (patientJson.name) {
          display += " - ";
        }
      }
      if (patientJson.name) {
        display += patientJson.name
      }
      if (closeBracket) {
        display += ")"
      }

      return display;
    }

    function displayMedicalReports(reportsJson)
    {
      var displayString = "";
      $.each(reportsJson, function (title, link)
      {
        displayString += displayMedicalReport(title, link);
      });
      return displayString;
    }

    function displayMedicalReport(title, link)
    {
      return "<div><a href='" + link + "' target='_blank'>" + title + "</a></div>"
    }
  });
});
