var PhenoTips = (function (PhenoTips) {
  var widgets = PhenoTips.widgets = PhenoTips.widgets || {};
  widgets.PushPatientWidget = Class.create({
    initialize : function() {
      // ---------------------------------------------------------------------
      // Search for the push patient elements. Exist if missing.
      // ---------------------------------------------------------------------
      this._pushManyPatients = false;

      var pushTargets = $$("div[id^=push-patient]");
      if (pushTargets.length == 0) {
          pushTargets = $$("a[id^=pushall-server]");
          if (pushTargets.length == 0) {
              return;
          }
          this._pushManyPatients = true;
      }

      this._serviceURL = new XWiki.Document('PushPatientService', 'PhenoTips').getURL('get', 'outputSyntax=plain');
      this._patientId  = XWiki.currentDocument.page;
      //if (!this._pushManyPatients)
      //    console.log("PUSH UI: Patient: " + this._patientId + ", serviceURL: " + this._serviceURL);
      //else
      //    console.log("PUSH UI: multipush, serviceURL: " + this._serviceURL);

      // ---------------------------------------------------------------------
      // Initialize the UI
      // ---------------------------------------------------------------------
      this._initUI();

      // ---------------------------------------------------------------------
      // Attach launcher behavior:
      // ---------------------------------------------------------------------
      var _this = this;

      this._observeSave = function() {
          _this._launchUI();
      }

      for (var server = 0; server < pushTargets.length; server++) {
          var nextServer = pushTargets[server];
          var pushLauncher = this._pushManyPatients ? nextServer : nextServer.down('a');
          if (!pushLauncher) continue;

          var serverName = nextServer.readAttribute("name");

          var createLauncher = function(serverID) {
            return function(event) {
	            event.stop();
	            event.findElement().blur();

	            _this._selectedServer = serverID;
	            //console.log("Initializing push to " + serverID);

	            if (event.findElement('.editbody')) {
	              // we're in edit mode; ask user if they want to save before exporting the data
	              new XWiki.widgets.ConfirmationBox(
	                {
	                  onYes : function() {
	                    document.fire('xwiki:actions:save', {'continue' : true, 'form' : event.findElement('form')});
	                    document.observe('xwiki:document:saved', _this._observeSave);
	                  },
	                  onNo : function() {
	                    _this._launchUI();
	                  }
	                },
	                /* Interaction parameters */
	                { confirmationText: "$services.localization.render('phenotips.PushPatient.confirmationText')", showCancelButton: true }
	              );
	            } else {
	              _this._launchUI();
	            }
	          }
          }
	      pushLauncher.observe('click', createLauncher(serverName));
	  }
      // ---------------------------------------------------------------------
      // Initialization done
      // ---------------------------------------------------------------------
    },

    _initUI: function() {
      var generateSection = function(id, title, intro) {
         return new Element('div', {'id': id, 'class' : 'section'})
                    .insert(title && new Element('h2').update(title) || '')
                    .insert(intro && new Element('p', {'class' : 'intro xHint'}).update(intro) || '')
                    .insert(new Element ('div', {'class' : 'section-contents'}));
      };

      var serverTitle = this._pushManyPatients ? "$services.localization.render('phenotips.PushPatient.titleAllPaitents')":
                                                "$services.localization.render('phenotips.PushPatient.titleOnePaitent')";
      var serverDesc  = this._pushManyPatients ? "$services.localization.render('phenotips.PushPatient.serverAllPaitents')":
                                                 "$services.localization.render('phenotips.PushPatient.serverOnePaitent')";
      this._container = new Element('form', {'id' : 'push-patients-ui', 'class' : 'xform', 'method' : 'post', 'action' : this._serviceURL});
      this._container.insert(generateSection('server-selection', serverTitle, serverDesc))
                     .insert(generateSection('user-selection', "$services.localization.render('phenotips.PushPatient.authenticationTitle')","$services.localization.render('phenotips.PushPatient.authenticationHint')"))
                     .insert(generateSection('group-selection',"$services.localization.render('phenotips.PushPatient.remoteGroupTitle')","$services.localization.render('phenotips.PushPatient.remoteGroupHint')"));
      if (!this._pushManyPatients) {
          this._container.insert(generateSection('fields-selection',"$services.localization.render('phenotips.PushPatient.previewTitle')","$services.localization.render('phenotips.PushPatient.previewHint')"));
      }

      this._checkBoxApprove = new Element('input',{type:'checkbox', 'id':'approve-checkbox', 'value':'approve'});
      var labelApprove = new Element('label', {'id':'approve-label'});
      var approveMessage = this._pushManyPatients ? "$services.localization.render('phenotips.PushPatient.approveMessageMany')":
                                                    "$services.localization.render('phenotips.PushPatient.approveMessageOne')";
      labelApprove.insert(this._checkBoxApprove).insert(approveMessage);
      this._approveElement = new Element('div', {'class' : 'confirm-push plainmessage'});
      this._approveElement.insert(labelApprove);
      if (this._pushManyPatients) {
          this._container.insert("<br/>");
      }
      this._container.insert(this._approveElement);

      this._serverManager = this._container.down('#server-selection .section-contents');
      this._userManager   = this._container.down('#user-selection .section-contents');
      this._groupManager  = this._container.down('#group-selection .section-contents');
      this._fieldsManager = this._pushManyPatients ? null : this._container.down('#fields-selection .section-contents');

      this._initServerSelector();
      this._initMainFormActions();

      var title = this._pushManyPatients ? "$services.localization.render('phenotips.PushPatient.titleMany')" : "$services.localization.render('phenotips.PushPatient.titleOne')".replace("__patientID__", this._patientId);
      this._dialog = new PhenoTips.widgets.ModalPopup(this._container, false, {'title': title, 'verticalPosition': 'top', 'removeOnClose': false});
    },

    _generateServerFirstMessage: function() {
      return new Element('div', {'class' : 'warningmessage'}).update("$services.localization.render('phenotips.PushPatient.serverFirstMessage')");
    },

    _generateServerAndUserFirstMessage: function() {
      return new Element('div', {'class' : 'warningmessage'}).update("$services.localization.render('phenotips.PushPatient.serverAndUserFirstMessage')");
    },

    _generateNoPatientJSONMessage: function(fieldList) {
      var retryButton = new Element('input', {type: 'button', name : 'retry', 'value': "$services.localization.render('phenotips.PushPatient.retry')", 'class' : 'button secondary'}).wrap('span', {'class' : 'buttonwrapper'});
      var _this = this;
      retryButton.observe('click', function(event) {
        _this._generateDataPreview(fieldList);
      });
      return new Element('div', {'class' : 'errormessage'}).update("$services.localization.render('phenotips.PushPatient.noPatientMessage')").insert(retryButton);
    },

    _generateNoServerListMessage: function() {
      var retryButton = new Element('input', {type: 'button', name : 'retry', 'value': "$services.localization.render('phenotips.PushPatient.retry')", 'class' : 'button secondary'}).wrap('span', {'class' : 'buttonwrapper'});
      var _this = this;
      retryButton.observe('click', function(event) {
        _this._launchUI();
      });
      return new Element('div', {'class' : 'errormessage'}).update("$services.localization.render('phenotips.PushPatient.noServerListMessage')").insert(retryButton);
    },

    _generateCantConnectToServerMessage: function(failMessage, username, password) {
      var retryButton = new Element('input', {type: 'button', name : 'retry', 'value': "$services.localization.render('phenotips.PushPatient.retry')", 'class' : 'button secondary'}).wrap('span', {'class' : 'buttonwrapper'});
      var _this = this;
      retryButton.observe('click', function(event) {
        _this._loginAndGetConfig(username, password);
      });
      return new Element('div', {'class' : 'errormessage'}).update(failMessage + " ").insert(retryButton);
    },

    _generateNoPushServerConfiguredMessage: function() {
          return new Element('div', {'class' : 'errormessage'}).update("$services.localization.render('phenotips.PushPatient.noPushServerConfiguredMessage')");
    },

    _generateIncorrectCredentialsMessage: function() {
          return new Element('div', {'class' : 'errormessage'}).update("$services.localization.render('phenotips.PushPatient.noPushServerConfiguredMessage')");
    },

    _generateFailedToPushPatient: function(errorMessage, retryButton, retryAsNew) {
      var result = new Element('div', {'class' : 'errormessage'}).update(errorMessage);

      if (retryButton)
      {
        var retryButton = new Element('input', {type: 'button', name : 'retry', 'value': "$services.localization.render('phenotips.PushPatient.retry')", 'class' : 'button secondary'}).wrap('span', {'class' : 'buttonwrapper'});
        var _this = this;
        retryButton.observe('click', function(event) {
          _this._pushPatient();
        });
        result.insert(retryButton);
      }
      if (retryAsNew)
      {
        var retryAsNewButton = new Element('input', {type: 'button', name : 'retry', 'value': "$services.localization.render('phenotips.PushPatient.retryAsNew')", 'class' : 'button secondary'}).wrap('span', {'class' : 'buttonwrapper'});
        var _this = this;
        retryAsNewButton.observe('click', function(event) {
          _this._selectPushNewPatient();
          _this._pushPatient();
        });
        result.insert(retryAsNewButton);
      }
      return result;
    },

    _onUnapprovedUser: function() {
      this._patientData = undefined;

      this._groupManager.update(this._generateServerAndUserFirstMessage());
      !this._pushManyPatients && this._fieldsManager.update(this._generateServerAndUserFirstMessage());

      this._pushButton.disable();   // disabled until the server is selected and fields are confirmed
      this._approveElement.hide();

      this._pushResultSection.update("");

      this._patientFieldList = undefined;
      this._userGroupsList   = undefined;
    },

    _onNoServerSelected: function() {
      this._lastApprovedUser  = undefined;
      this._lastSelectedGroup = undefined;
      this._userLoginError    = undefined;

      this._serverInfo.hide();
      this._userManager.update(this._generateServerFirstMessage());

      this._onUnapprovedUser();
    },

    _disableControl: function(control) {
        // disables the control and makes sure that even if all controls are now disabled
        // and will get re-enabled later this one wont get re-enabled
        control.disable();
        control.__wasDisabledState = true;  // see _disableInputs()
    },

    _disableInputs: function(disableCancelAsWell) {
       // disable all UI elements while waiting for AJAX rerquest to complete
       this._container.addClassName('loading');
       var allInputs = this._container.getElementsByTagName('input');
       for (var i = 0; i < allInputs.length; i++) {
          if (allInputs[i].disabled) {
            allInputs[i].__wasDisabledState = true;
          } else {
            allInputs[i].disable();
          }
       }
       // disable server-selector: it is an option not an input
       this._serverSelector.down('select').disable();
       // but enable the cancel button
       if (!disableCancelAsWell) {
          this._cancelButton.enable();
       }
    },

    _restoreInputs: function() {
       this._container.removeClassName('loading');
       // but do not enable what was disbaled before
       var allInputs = this._container.getElementsByTagName('input');
       for (var i = 0; i < allInputs.length; i++) {
          if  (allInputs[i].__wasDisabledState) {
             delete(allInputs[i].__wasDisabledState);
          } else {
             allInputs[i].enable();
          }
       }
       // enable server-selector: it is an option not an input
       this._serverSelector.down('select').enable();
    },

    _launchUI : function() {
      var _this = this;
      document.stopObserving('xwiki:document:saved', _this._observeSave);

      if (this.__launchUIAjaxInProgress) { return; }

      // reset data about available push servers
      this._remoteServers       = {};
      this._numAvailableServers = 0;

      this._onNoServerSelected();

      this._serverLoadMessages.update(new Element('div', {'class' : 'infomessage'}).update("$services.localization.render('phenotips.PushPatient.retrievingListMessage')"));
      this._serverLoadMessages.show();
      this._serverSelector.hide();
      this._container.addClassName('loading');

      //AJAX 1: Get available remote servers
      new Ajax.Request(this._serviceURL, {
        method: _this._container.method,
        parameters : {'do':'getremotes', 'patientId': _this._patientId},
        onCreate : function() {
          _this.__launchUIAjaxInProgress = true;
          _this._dialog.showDialog();
          _this._container.up('.msdialog-modal-container').style.zIndex = 3001;
        },
        onSuccess : function (response) {
          console.log("PUSH: Got response for the getremotes request");
          var data = response.responseJSON;

          if (!data || data.length == 0) {
            //console.log("PUSH: no remotes defined");
            // if there are no configured servers, display warning message
            _this._serverLoadMessages.update(_this._generateNoPushServerConfiguredMessage());
          } else {
            //console.log("PUSH: Found " + data.length + " remotes: " + stringifyObject(data));
            // otherwise show the list of servers to pick from
            _this._serverLoadMessages.hide();
            _this._populateServerList(data);
          }
        },
        onFailure : function(response) {
          _this._serverLoadMessages.update(_this._generateNoServerListMessage());
        },
        on0 : function (response) {
          response.request.options.onFailure(response);
        },
        onComplete : function() {
          _this.__launchUIAjaxInProgress = false;
          _this._container.removeClassName('loading');
          console.log("PUSH: getting remotes - complete");
        }
      });
    },

    _queryStoredUserName: function() {
      var _this = this;
      this._userManager.update("");
      //AJAX 2: Get the user name last used for this server
      new Ajax.Request(this._serviceURL, {
        method: _this._container.method,
        parameters : {'do':'getuser', 'serverid': this._selectedServer },
        onCreate : function() {
          console.log("PUSH: get username - start");
          _this._disableInputs();
        },
        onSuccess : function (response) {
          //console.log("PUSH: Got user last used for the server (JSON: " + stringifyObject(response.responseJSON) + ")");
          if (response.responseJSON.remoteUserName) {
            _this._lastApprovedUser = response.responseJSON.remoteUserName;
          }
        },
        onFailure : function(response) {
          console.log("PUSH: couldn't not get the user last used for this server");
          // just ignore the failure and let user manualy enter username/password
        },
        on0 : function (response) {
          response.request.options.onFailure(response);
        },
        onComplete : function() {
          _this._restoreInputs();
          console.log("PUSH: getting username - complete");
          if (_this._lastApprovedUser) {
              _this._loginAndGetConfig(_this._lastApprovedUser, null);  // attempt a password-less login using stored token
          }
          else {
              _this._updateUserList();
          }
        }
      });
    },

    _loginAndGetConfig: function(username, password) {
      console.log("loginAndGetConfig: probing " + username);

      var _this = this;
      // TODO: suport workflow when tokens are not enabled e.g. on the server side (have to remember username/password in JS)
      var params = {'do':'getremoteconfig', 'serverid': this._selectedServer, 'savetoken':true };
      if (password) {
        params['usr'] = username;
        params['pwd'] = password;
      }     // else atempt a passwordless login using the stored username

      //AJAX 3: attempt to authorize on the remote server + get remote config
      new Ajax.Request(this._serviceURL, {
        method: _this._container.method,
        parameters : params,
        onCreate : function() {
          console.log("PUSH: get remote config - start");
          _this._disableInputs();
        },
        onSuccess : function (response) {
          if (response.hasOwnProperty("responseJSON") && response.responseJSON) {
            console.log("PUSH: Got response from server: " + stringifyObject(response.responseJSON));
            _this._lastLoginResponse = response.responseJSON;    // TODO: store per user+server locally, so that there is no need to AJAX again for the same user if selected again?
          } else {
            console.log("PUSH: No response from local service");
            _this._lastLoginResponse =  {"status":"error", "nolocalresponse":true};
          }
        },
        onFailure : function(response) {
          console.log("PUSH: unable to get a response from remote server");
          _this._lastLoginResponse = {"status":"error", "serverconnectproblem":true};  // simulate "unable to connect" response from script service
        },
        on0 : function (response) {
          response.request.options.onFailure(response);
        },
        onComplete : function() {
          this._restoreInputs();
          console.log("PUSH: get remote config - complete");

          if (this._lastLoginResponse.status == "success") {
            console.log("--> login successful!");
            this._lastApprovedUser = username;
            this._updateUserList();  // update current user list with an indication that this user has successfully logged in
            this._populateGroupsAndData(this._lastLoginResponse.groups, this._lastLoginResponse.serverfields, this._lastLoginResponse.updatesEnabled);
          }
          else {
            console.log("--> login failed!");
            if (this._lastApprovedUser == username) {
              this._lastApprovedUser = undefined;   // this user is no longer approved
            }
            this._updateUserList(username, this._lastLoginResponse, username, password);
          }
        }.bind(this)
      });
    },

    _logoutUser: function() {
      var _this = this;
      var params = {'do':'removetokens', 'serverid': this._selectedServer };

      new Ajax.Request(this._serviceURL, {
        method: _this._container.method,
        parameters : params,
        onCreate : function() {
          console.log("PUSH: logout user");
          try{
          _this._disableInputs();
          } catch (e) {console.log("ERROR: " + e); }
        },
        onSuccess : function (response) {
          _this._lastLoginResponse = undefined;
          _this._lastApprovedUser  = undefined;
          _this._updateUserList();
        },
        onFailure : function(response) {
          alert("Failed to log out");
        },
        on0 : function (response) {
          response.request.options.onFailure(response);
        },
        onComplete : function() {
          this._restoreInputs();
          console.log("PUSH: logout - complete");
        }.bind(this)
      });
    },

    _updateUserList: function(selectedUserName, lastLoginResponse, user_when_failed, password_when_failed) {
      if (lastLoginResponse) {
        if (this._lastLoginResponse.nolocalresponse) {
          this._userManager.update(this._generateCantConnectToServerMessage("$services.localization.render('phenotips.PushPatient.noLocalResponse')", user_when_failed, password_when_failed));
          return;
        }
        if (this._lastLoginResponse.unauthorizedserver) {
          this._userManager.update(this._generateCantConnectToServerMessage("$services.localization.render('phenotips.PushPatient.unauthorizedServer')", user_when_failed, password_when_failed));
          return;
        }
        if (this._lastLoginResponse.serverconnectproblem) {
          this._userManager.update(this._generateCantConnectToServerMessage("$services.localization.render('phenotips.PushPatient.serverConnectProblem')", user_when_failed, password_when_failed));
          return;
        }
      }

      console.log("updateUserList: " + selectedUserName + ", last approved: " + this._lastApprovedUser);
      var _this = this;

      var userList = new Element('table', {id : 'user-list'});

      // either show only the username/password inputs + "authorize" button or
      // a radiobox [last approved user] and [the inputs + button above]
      // => re-login at every change

      // 1) if `lastApprovedUser` and `selectedUserName` are not set: only username/password inputs (selected)
      // 1) if `lastApprovedUser` is not set and `selectedUserName` is set: only username/password inputs (selected and prefilled)
      // 1) if `lastApprovedUser` is set and `selectedUserName` is not or equal to `lastApproved`: select lastApproved, plus an option to enter username/password
      // 2) if `lastApprovedUser` is set and `selectedUserName` is also set and different: show username/password (selected & prefilled) + an option to switch to `lastApproved`

      var _addUserOption = function (checked, iconType, labelText, handlerWhenSelected, controlledElements) {
        var optionWrapper = new Element('tr');
        var input = new Element('input', {"type" : "radio", "value": labelText, "name": "select-user"});
        if (checked) {
          input.checked = true;
        }
        var label = new Element('label').insert(input).insert(labelText);
        optionWrapper.insert(new Element('span', {'class' : 'fa fa-' + iconType}).wrap('td')).insert(label.wrap('td'));

        if (controlledElements) {
          optionWrapper.insert(controlledElements.wrap('td', {'class' : 'controlled-element'}));
        }

        input.observe('change', function(event) {
             var trigger = event.findElement('input');

             // disable inputs for all other user options
             var inputs  = trigger.up('table').getElementsByTagName('input');
             for (var i = 0; i < inputs.length; i++) {
                if (inputs[i].getAttribute("name") != "select-user")
                    inputs[i].disable();
             }
             // enable inputs for this option
             var inputs  = trigger.up('tr').getElementsByTagName('input');
             for (var i = 0; i < inputs.length; i++) {
                 inputs[i].enable();
             }

             handlerWhenSelected();
        });
        return optionWrapper;
      };

      var preApprovedChecked = false;
      if (this._lastApprovedUser) {
        preApprovedChecked = !selectedUserName || (this._lastApprovedUser == selectedUserName);
        var forgetmeBox = new Element('input', {type: 'button', name : 'logout', 'value': "$services.localization.render('phenotips.PushPatient.logOut')", 'class' : 'button secondary'}).wrap('span', {'class' : 'buttonwrapper'});
        forgetmeBox.observe('click', function(event) {
           this._onUnapprovedUser();
           this._logoutUser();
        }.bind(this));
        var preApprovedOption = _addUserOption(preApprovedChecked, "check", "$services.localization.render('phenotips.PushPatient.currentlyAuthenticatedMessage')".replace("__lastApprovedUser__", "<strong>"+this._lastApprovedUser+"</strong> &nbsp;"),
                                               function(){
                                                 this._userLoginError.hide();
                                                 this._onUnapprovedUser();
                                                 this._loginAndGetConfig(this._lastApprovedUser);
                                               }.bind(this), forgetmeBox);
        userList.insert(preApprovedOption);
      }

      // add an option to enter username & password manually
      var userPasswrodBox = new Element("span", {'class' : 'user-password-box'});
      var userNameBox = new Element("input", {type: 'text', "id": "newusername", "value": "", "placeholder": "$services.localization.render('phenotips.PushPatient.userName')", size: 12});
      if (selectedUserName) {
        userNameBox.value = selectedUserName;
      }
      var passwordBox = new Element("input", {type:'password', "id": "password", "value": "", "placeholder": "$services.localization.render('phenotips.PushPatient.password')", size: 12});
      var authorizeButton = new Element('input', {type: 'button', id:"authorizenewuser", 'name':'authorize', 'value':"$services.localization.render('phenotips.PushPatient.logIn')", 'class':'button secondary'});
      authorizeButton.observe('click', function(event) {
         if (passwordBox.value != "") {
             this._userLoginError.hide();
             this._loginAndGetConfig(userNameBox.value, passwordBox.value);
         } else {
             this._userLoginError.update("$services.localization.render('phenotips.PushPatient.noPassword')");
             this._userLoginError.show();
         }
      }.bind(this));

      if (preApprovedChecked) {
         userNameBox.disable();
         passwordBox.disable();
         authorizeButton.disable();
      }
      userPasswrodBox.insert(userNameBox.wrap('label', {'class' : 'fa fa-user'}))
                     .insert(" ")
                     .insert(passwordBox.wrap('label', {'class' : 'fa fa-key'}))
                     .insert(" ")
                     .insert(authorizeButton.wrap('span', {'class' : 'buttonwrapper'}));

      if (this._lastApprovedUser) {
          userList.insert(_addUserOption(!preApprovedChecked, "user", "$services.localization.render('phenotips.PushPatient.addAnotherUser')", this._onUnapprovedUser.bind(this), userPasswrodBox));
      } else {
          userList.insert(userPasswrodBox.wrap('td', {'class' : 'controlled-element'}).wrap('tr'));
      }

      this._userLoginError = new Element('div', {'class' : 'errormessage'});
      this._userLoginError.hide();
      this._userManager.update(userList).insert(this._userLoginError);

      if (lastLoginResponse) {
        if (this._lastLoginResponse.loginfailed && password_when_failed != "") {   // ignore unseccessful passwrodless logins - maybe token has expired, it is OK
          this._userLoginError.update("$services.localization.render('phenotips.PushPatient.cantAuthorizeUser')");
          this._userLoginError.show();
        }
      }
    },

    // ---------------------------------------------------------------------
    // Display group picker and data preview - once server/user are known
    // ---------------------------------------------------------------------
    _populateGroupsAndData: function(groupList, fieldList, serverUpdatesEnabled) {
      //console.log("Fields: " + stringifyObject(fieldList));
      if (!serverUpdatesEnabled) {
          this._remoteServers[this._selectedServer]["noUpdates"] = true;
      }
      this._displayGroupPicker(groupList);
      if (this._pushManyPatients) {
        this._showApproveCheckbox();
      } else {
        this._generateDataPreview(fieldList);
      }
    },

    _displayGroupPicker: function(groupList) {
      if (!groupList) {
        groupList = this._userGroupsList;
        if (!this._userGroupsList) return;
      }
      else {
        this._userGroupsList = groupList;
      }
      console.log("Groups: " + stringifyObject(groupList));

      var groupListElement = new Element('table', {id : 'user-list'});

      var _addGroupOption = function (checked, iconType, labelText, value) {
        var optionWrapper = new Element('tr');
        var input = new Element('input', {"type" : "radio", "value": value, "name": "select-group"});
        if (checked) {
          input.checked = true;
        }
        var label = new Element('label').insert(input).insert(labelText);
        optionWrapper.insert(new Element('span', {'class' : 'fa fa-' + iconType}).wrap('td')).insert(label.wrap('td'));
        return optionWrapper;
      };

      if (!this._updatingExistingPatient())  {
          var noGroupMessage = "<span class ='hint'>$services.localization.render('phenotips.PushPatient.noGroupMessage')</span>";
          if (groupList.length == 0) {
            noGroupMessage += " $services.localization.render('phenotips.PushPatient.noGroupMessageHint')".replace("__lastApprovedUser__", this._lastApprovedUser);
          }
          groupListElement.insert(_addGroupOption(true, "user", noGroupMessage, "__self"));

          groupList.each(function (group) {
                groupListElement.insert(_addGroupOption(false, "group", group, group));
            });
      } else {
          var msg = "$services.localization.render('phenotips.PushPatient.keepGroupMessage')";
          msg += " <span class ='hint'>$services.localization.render('phenotips.PushPatient.keepGroupMessageHint')</span>"
          groupListElement.insert(_addGroupOption(true, "check", msg, "__self"));
      }

      this._groupManager.update(groupListElement);

      if (this._remoteServers[this._selectedServer]["noUpdates"]) {
          this._disableUpdateOption();
      }
    },

    _generateDataPreview: function(fieldList) {
      this._patientFieldList = fieldList;
      var _this = this;
      //AJAX 4: local AJAX request to get patient JSON to let the user see the actual data being pushed
      new Ajax.Request(this._serviceURL, {
        method: _this._container.method,
        parameters : { "do":"getpatientjson", "patientid":_this._patientId, "fields":Object.toJSON(this._patientFieldList) },
        onCreate : function() {
          console.log("PUSH: get patient JSON - start");
          _this._disableInputs();
        },
        onSuccess : function (response) {
          console.log("PUSH: Got response from server: " + stringifyObject(response.responseJSON));
          _this._displayPatientData(response.responseJSON);
        },
        onFailure : function(response) {
          console.log("PUSH: unable to get patient JSON");
          _this._fieldsManager.update(_this._generateNoPatientJSONMessage(fieldList));
        },
        on0 : function (response) {
          response.request.options.onFailure(response);
        },
        onComplete : function() {
          _this._restoreInputs();
          console.log("PUSH: get patient JSON - complete");
        }
      });
    },

    _displayPatientData: function(patientData) {
      this._showApproveCheckbox();
      this._patientData = patientData;
      //console.log("Data: " + stringifyObject(patientData));
      this._fieldsManager.update(this._generatePatientDataPreview(patientData));
    },

    _showApproveCheckbox: function() {
      this._checkBoxApprove.checked = false;
      this._checkBoxApprove.enable();
      this._approveElement.show();
    },

    _generatePatientDataPreview: function(patientJSON) {
        var dataCategories = [
           {
              'name'     : 'external_id',
              'title'    : "$services.localization.render('phenotips.PushPatient.dataPreviewIdentifier')",
              'callback' : '__displayId'
           },
           {
              'name'     : 'sex',
              'title'    : "$services.localization.render('phenotips.PushPatient.dataPreviewSex')",
              'callback' : '__displaySex'
           },
           {
              'name'     : 'features',
              'title'    : "$services.localization.render('phenotips.PushPatient.dataPreviewFeatures')",
              'callback' : '__displayFeatures'
           },
           {
              'name'     : 'disorders',
              'title'    : "$services.localization.render('phenotips.PushPatient.dataPreviewDisorders')",
              'callback' : '__displayFeatures'
           },
        ];
        var result = new Element('dl');
        var _this = this;
        dataCategories.each(function(c) {
           patientJSON[c.name] && _this[c.callback] && result.insert(new Element('dt').update(c.title || c.name)).insert(_this[c.callback](patientJSON[c.name]).addClassName('data-preview-section')); 
        });
        return result;
    },

    __displayId : function(id) {
        return new Element('dd').insert(id);
    },
    __displaySex : function(sex) {
        return new Element('dd').insert((sex == "F") ? "$services.localization.render('phenotips.PushPatient.female')" : (sex == "M" ? "$services.localization.render('phenotips.PushPatient.male')" : "$services.localization.render('phenotips.PushPatient.unknown')"));
    },

    __displayFeatures : function(features) {
        var result = new Element('dd');
        features.each(function (f) {
          result.insert(new Element('div', {'class' : (f.observed == "no" ? "negative" : "")}).update((f.observed == "no" ? "$services.localization.render('phenotips.PushPatient.negative')" : "") + f.label).insert(f.id && new Element('span', {'class' : 'hint'}).update(' (' + f.id + ')')  || '') );
        });
        return result;
    },

    // ---------------------------------------------------------------------
    // Generate server selection UI
    // ---------------------------------------------------------------------
    _populateServerList: function(pushServerList) {
      //console.log("Server list input: " + stringifyObject(pushServerList));

      this._remoteServers = {};
      var servers = [];

      this._numAvailableServers = pushServerList.length;
      for(var i = 0; i < pushServerList.length; i++) {
          var serverInfo = pushServerList[i].serverinfo;
          var pushInfo   = pushServerList[i].pushinfo;
          var name       = serverInfo.serverID;
          servers.push(name);

          serverInfo.serverURL = this._addHTTP(serverInfo.serverURL);

          var pushAge    = pushInfo ? pushInfo.lastPushAgeInHours : -1;
          var remoteID   = pushInfo ? pushInfo.remotePatientID : "";
          var remoteGUID = pushInfo ? pushInfo.remotePatientGUID : "";
          var remoteURL  = pushInfo ? (serverInfo.serverURL + pushInfo.remotePatientURL) : "";
          this._remoteServers[name] = {"url": serverInfo.serverURL,
                                       "desc": serverInfo.serverDescription,
                                       "pushAgeHours": pushAge,
                                       "remoteID": remoteID,
                                       "remoteGUID": remoteGUID,
                                       "remoteURL": remoteURL};
      }
      servers.sort();
      var _this = this;

      //console.log("Remote servers as cached: " + stringifyObject(this._remoteServers))

      this._selectServer(this._selectedServer);
    },

    _selectServer: function(serverName, keepUserAndGroup) {
        this._selectedServer = serverName;
        var serverData = this._remoteServers[serverName];
        var serverLink = new Element('a', {'href' : serverData.url, 'target' : '_blank', 'class' : 'remote-server-name'}).update(this._selectedServer);

        if (this._numAvailableServers == 1) {
            this._serverInfo.update("$services.localization.render('phenotips.PushPatient.oneServerMessage')");
        }

        var existingPatientDataInfo = '';

        if (serverData.pushAgeHours !== undefined && serverData.pushAgeHours >=0) {
           existingPatientDataInfo = new Element('dd');

           var description = new Element('p', {'id': 'server-description'});
           var lastUpdated = serverData.pushAgeHours;
           description.insert("$services.localization.render('phenotips.PushPatient.patientUploadedMessage')");
           if (lastUpdated < 1) {
              description.insert(" $services.localization.render('phenotips.PushPatient.patientUploadedLessThanHour')");
           } else {
              if (lastUpdated >= 48) {
                  description.insert(" " + Math.floor(lastUpdated/24) + " $services.localization.render('phenotips.PushPatient.patientUploadedDays')");
              } else {
                  description.insert(" " + lastUpdated + " $services.localization.render('phenotips.PushPatient.patientUploadedHours')");
              }
           }

           description.insert(" $services.localization.render('phenotips.PushPatient.patientUploadedAsID') ").insert(new Element('a', {'href' : serverData.remoteURL, 'target' : '_blank'}).update(serverData.remoteID)).insert('.');
           description.insert("<br/>$services.localization.render('phenotips.PushPatient.patientUploadedUpdate')")
           existingPatientDataInfo.insert(description);

           var _this = this;
           var selectPushType = new Element('p');
           var radio1 = new Element('input', {"type" : "radio", "value": "update", "id":"choice-update-patient", "name": "new-or-update"});
           radio1.checked = true;
           radio1.observe('change', function() { _this._displayGroupPicker() });
           var label1 = new Element('label', {"hidden": true}).insert(new Element('span', {'class' : 'fa fa-refresh'}).update(' ')).insert(radio1).insert("$services.localization.render('phenotips.PushPatient.updateRemotePatient')");
           var radio2 = new Element('input', {"type" : "radio", "value": "new", "id":"choice-new-patient", "name": "new-or-update"});
           radio2.observe('change', function() { _this._displayGroupPicker() } );
           var label2 = new Element('label', {"hidden": true}).insert(new Element('span', {'class' : 'fa fa-plus-square'}).update(' ')).insert(radio2).insert("$services.localization.render('phenotips.PushPatient.createRemotePatient')");
           existingPatientDataInfo.insert(label1).insert(label2);
        }

        this._serverInfo.update(new Element('dl')
                           .insert(serverLink.clone(true).wrap('dt'))
                           .insert(serverData.desc && new Element('dd', {'class' : 'hint'}).update(serverData.desc) || '')
                           .insert(existingPatientDataInfo || '')
        );

        if (!keepUserAndGroup) {
            this._serverInfo.show();
            this._queryStoredUserName();
        }
    },

    _onServerOptionChange: function() {
      var serverSelectInput = this._serverSelector.down('select');
      var selectedServer = serverSelectInput.options[serverSelectInput.selectedIndex].value;
      this._onNoServerSelected();
      if (selectedServer != 'none') {
        this._selectServer(selectedServer);
      }
    },

    _initServerSelector: function() {
      this._serverLoadMessages = new Element('div', {id: 'server-load-messages'});
      this._serverLoadMessages.hide();

      var options = new Element('select', {id: 'server-selector'});
      options.insert(new Element('option', {value: "none"}).update("$services.localization.render('phenotips.PushPatient.loadingServers')"));
      options.observe('change', this._onServerOptionChange.bind(this));   // on server selection: enable & repopulate user/group lists

      this._serverSelector = new Element('div', {id: 'server-selector-div'});
      this._serverSelector.hide();
      this._serverSelector.update(options);

      this._serverInfo = new Element('div', {id: 'server-info'});

      this._serverManager.update(this._serverLoadMessages).insert(this._serverSelector ).insert(this._serverInfo);
    },

    // ---------------------------------------------------------------------
    // Generate and handle main "Cancel" and "Push" buttons
    // ---------------------------------------------------------------------
    _initMainFormActions : function() {
      var _this = this;
      var buttons = new Element('div', {'class' : 'buttons'});
      buttons.insert(new Element('input', {type: 'hidden', name : 'xaction', 'value': 'push'}));
      buttons.insert(new Element('input', {type: 'hidden', name : 'patient', 'value': this._patientId}));
      var pushButtonTitle = this._pushManyPatients ? "$services.localization.render('phenotips.PushPatient.button.manyPatients.submit')" : "$services.localization.render('phenotips.PushPatient.button.push')";
      buttons.insert(new Element('input', {type: 'submit', name : 'submit',  'value': pushButtonTitle, 'class' : 'button', 'id': 'push_patient_button'}).wrap('span', {'class' : 'buttonwrapper'}));
      buttons.insert(new Element('input', {type: 'button', name : 'close',   'value': "$services.localization.render('phenotips.PushPatient.button.cancel')", 'class' : 'button secondary'}).wrap('span', {'class' : 'buttonwrapper'}));
      this._container.insert(buttons);

      this._pushResultSection = new Element ('div', {'class' : 'section-contents'});
      this._container.insert(this._pushResultSection);

      this._cancelButton = buttons.down('input[name="close"]');
      this._cancelButton.observe('click', function(event) {
        _this._dialog.closeDialog();
      })

      this._pushButton = buttons.down('input[name="submit"]');
      this._checkBoxApprove.observe("click", function(event) {
        if (_this._checkBoxApprove.checked) {
          _this._pushResultSection.update("");
          _this._pushButton.enable();
        }
        else
          _this._pushButton.disable();
          _this._pushResultSection.update("");
      });

      this._container.observe('submit', function(event) {
        event.stop();
        if (_this._pushManyPatients) {
            _this._pushMultiplePatients();
        } else {
            _this._pushPatient();
        }
      })  // observe('submit')
    },

    _updatingExistingPatient: function() {
        var pushUpdate = false;
        var radioSelectUpdate = document.getElementById('choice-update-patient');
        if (radioSelectUpdate) {
            pushUpdate = radioSelectUpdate.checked;
        }
        return pushUpdate;
    },

    _selectPushNewPatient: function() {
        var radioSelectUpdate = document.getElementById('choice-update-patient');
        var radioSelectNew    = document.getElementById('choice-new-patient');
	if (radioSelectUpdate && radioSelectNew) {
            radioSelectUpdate.checked = false;
            radioSelectNew.checked    = true;
        }
    },

    _disableUpdateOption: function() {
        var radioSelectNew = document.getElementById('choice-new-patient');
        if (radioSelectNew) {
            radioSelectNew.checked = true;
        }
        var radioSelectUpdate = document.getElementById('choice-update-patient-label');
        if (radioSelectUpdate) {
            var serverDesc = document.getElementById('server-description');
            serverDesc.insert("<p>$services.localization.render('phenotips.PushPatient.serverDesc')</p>");
            radioSelectUpdate.hide()
        }
    },

    _pushPatient: function(patientID, callBack, columnList) {
        var _this = this;
        if (_this._pushManyPatients && (!patientID || !callBack)) {
            return;
        }
        if (!_this._pushManyPatients) {
            _this._pushResultSection.update("");
        }

        var patientID = patientID ? patientID : this._patientId;

        var params = {"do":"push", 'serverid': this._selectedServer, 'patientid': patientID};

        params["fields"] = columnList ? Object.toJSON(columnList) : Object.toJSON(this._patientFieldList);

        if (_this._pushManyPatients){
            params["guid"] = "auto";
        } else if (this._updatingExistingPatient()) {
            params["guid"] = _this._remoteServers[_this._selectedServer].remoteGUID;
        }

        var radioSelectValue = $$('input:checked[type=radio][name="select-group"]')[0].value;
        if (radioSelectValue != "__self") {
            params["groupname"] = radioSelectValue;
        }

        console.log("PUSH request params: " + stringifyObject(params));

        new Ajax.Request(this._serviceURL, {
          method: _this._container.method,
          parameters : params,
          onCreate : function() {
            _this._disableInputs(true /* disable cancel button as well */);
          },
          onSuccess : function(response) {
            try {
                console.log("Got response: " + stringifyObject(response.responseJSON));
                var res = response.responseJSON;
                if (_this._pushManyPatients) {
                    callBack(res, patientID);
                    return;
                }
                if (res.status == "success") {
                    _this._checkBoxApprove.checked = false;

                    // update cached value for how this patient is represented on this remote server
                    var newURL = _this._remoteServers[_this._selectedServer].url + res.patienturl;
                    _this._remoteServers[_this._selectedServer]["pushAgeHours"] = 0;
                    _this._remoteServers[_this._selectedServer]["remoteID"]     = res.patientid;
                    _this._remoteServers[_this._selectedServer]["remoteGUID"]   = res.patientguid;
                    _this._remoteServers[_this._selectedServer]["remoteURL"]    = newURL;

                    if (_this._updatingExistingPatient()) {
                        var linkToOld = "(<a href='" + newURL + "' target='_blank'>click here to open remote patient</a>)";
                        _this._pushResultSection.update(new Element('div', {'class' : 'infomessage'}).update("$services.localization.render('phenotips.PushPatient.successfulUpdate') " + linkToOld));
                    } else {
                        var linkToNew = "<a href='" + newURL + "' target='_blank'>" + res.patientid + "</a>";
                        _this._pushResultSection.update(new Element('div', {'class' : 'infomessage'})
                                                .update("$services.localization.render('phenotips.PushPatient.successfulPush') " + linkToNew));
                    }

                    // update UI to reflect that we have already pushed this patient: default option
                    // is now "update" and the group choice should now be correspondingly removed
                    _this._selectServer(_this._selectedServer, true);
                    _this._displayGroupPicker();
                } else {
                    if (res.updatesdisabled) {
                        _this._pushResultSection.update(_this._generateFailedToPushPatient("$services.localization.render('phenotips.PushPatient.pushFailedUpdatesDisabled')"));
                    } else
                    if (res.invalidguid) {
                        _this._pushResultSection.update(_this._generateFailedToPushPatient("$services.localization.render('phenotips.PushPatient.pushFailedInvalidGuid')&nbsp;", false, true));
                    } else
                    if (res.accessdeniedguid) {
                        _this._pushResultSection.update(_this._generateFailedToPushPatient("$services.localization.render('phenotips.PushPatient.pushFailedAccessDeniedGuid')"));
                    } else
                    if (res.accessdeniedguid) {
                        _this._pushResultSection.update(_this._generateFailedToPushPatient("$services.localization.render('phenotips.PushPatient.pushFailedAccessDenied')"));
                    } else
                    if (res.cantconnect) {
                      _this._pushResultSection.update(_this._generateFailedToPushPatient("$services.localization.render('phenotips.PushPatient.pushFailedCantConnect')", true));
                    } else {
                      _this._pushResultSection.update(_this._generateFailedToPushPatient("$services.localization.render('phenotips.PushPatient.pushFailed')", true));
                    }
                }
                //_this._dialog.closeDialog();
              } catch (e) {
                console.log("EXCEPTION: " + e);
                _this._pushResultSection.update(_this._generateFailedToPushPatient("$services.localization.render('phenotips.PushPatient.pushFailedException')".replace("__e__", e), true));
              }
          },
          onFailure : function(response) {
            if (_this._pushManyPatients) {
                callBack({"status": "error"}, patientID);
                return;
            }
            var provideRetry  = false;
            var failureReason = response.statusText;
            if (response.statusText == '' || response.status == 12031 ) {   // no response + IE special case
               failureReason = 'Server not responding';
               provideRetry  = true;
            }
            _this._pushResultSection.update(_this._generateFailedToPushPatient(failureReason, provideRetry));
          },
          on0 : function (response) {
            if (_this._pushManyPatients) {
                callBack({"status": "error"}, patientID);
                return;
            }
            response.request.options.onFailure(response);
          },
          onComplete : function() {
            _this._restoreInputs();
            if (!_this._checkBoxApprove.checked) {
              _this._pushButton.disable();
            }
          }
        });
    },

    _addHTTP: function(url) {
        if (!/^(f|ht)tps?:\/\//i.test(url)) {
          url = "http://" + url;
        }
        return url;
    },

    _pushMultiplePatients: function() {
        var _this = this;

        var doMultiPush = function(patients, columns) {
            //console.log("Push with params: " + patients + " -> " + columns + " -> " + _this._selectedServer);
            _this._checkBoxApprove.checked = false;
            var pushResult = new Element("div", {"class": "multi-push-results infomessage"});
            _this._pushResultSection.update(pushResult);

            var next = 0;
            var pushSingleFunc = function(previousPushResult, prevPatientID) {
               // display previous push status
               if (previousPushResult && prevPatientID) {
                   if (previousPushResult.status != "success") {
                       pushResult.className = "multi-push-results errormessage";
                       if (previousPushResult.cantconnect) {
                           pushResult.insert(" $services.localization.render('phenotips.PushPatient.pushFailedCantConnect')");
                           return;
                       }
                       var reason = "failed.";
                       if (previousPushResult.invalidguid) {
                           reason = "$services.localization.render('phenotips.PushPatient.incorrectGuid')";
                       } else if (previousPushResult.accessdeniedguid) {
                           reason = "$services.localization.render('phenotips.PushPatient.accessDenied')";
                       }
                       pushResult.insert(' ' + reason + "<br/>");
                   } else {
                       pushResult.innerHTML = pushResult.innerHTML.replace(/Pushing (\w+?)\.\.\.$/, "");
                       var newURL       = _this._remoteServers[_this._selectedServer].url + previousPushResult.patienturl;
                       var linkToRemote = "<a href='" + newURL + "' target='_blank'>" + previousPushResult.patientid + "</a>";
                       pushResult.insert("<div class='pushed-ok-message'>" + "$services.localization.render('phenotips.PushPatient.successfulPushWithId')".replace("__prevPatientID__", prevPatientID).replace("__linkToRemote__", linkToRemote) + "</div>");
                   }
               }
               // push next
               if (next < patients.length) {
                   var nextPatientID = patients[next++];
                   pushResult.insert("$services.localization.render('phenotips.PushPatient.pushing')".replace("__nextPatientID__", nextPatientID));
                   _this._pushPatient(nextPatientID, pushSingleFunc, columns);
               }
            }

            pushSingleFunc();
        }
        var pushCallBack = function(patients, columns) {
            if (patients.length == 0) {
                return;
            }
            var confirmMessage = "$services.localization.render('phenotips.PushPatient.confirmMessage')".replace("__numPatients__", patients.length).replace("__selectServer__",  _this._selectedServer)+ "<br/><div class='plainmessage multi-push-patient-list'><ol>";
            patients.each(function(item) {
                confirmMessage += "<li>" + item + "</li>";
            });
            confirmMessage += "</ol></div>"

            new XWiki.widgets.ConfirmationBox(
              { onYes : function() { doMultiPush(patients, columns); }, },
              { confirmationText: confirmMessage, showCancelButton: false }
            );
        }
        var pushPreferencesFail = function() {
            alert("Failed to get the list of patients to be pushed");
        }
        var exportTool = $('phenotips_export');
        if (!exportTool) return;
        var dialog = new PhenoTips.widgets.ModalPopup('<img src="$xwiki.getSkinFile('icons/xwiki/ajax-loader-large.gif')"/>', false, {'title': "$services.localization.render('phenotips.pushPatient.exportTitle')", 'verticalPosition': 'top', 'removeOnClose': true, 'extraClassName': 'export-dialog'});
        dialog.showDialog();
        // =================================================================================
        // Generate the dialog content
        new Ajax.Request(new XWiki.Document('ExportPreferences', 'PhenoTips').getURL('get', 'space=' + /space=([^&]+)/.exec(exportTool.href)[1] + '&multipatient=true&remoteserver='+this._selectedServer), {
          onSuccess: function(transport) {
            var content = dialog.dialogBox._x_contentPlug;
            content.update(transport.responseText);
            content.__dialog = dialog;
            document.fire('xwiki:dom:updated', {'elements': [content],
                                                'multiPushPreferences': content,
                                                'callbackOK': pushCallBack,
                                                'callbackFail': pushPreferencesFail});
          }
        });
    }

  });
  return PhenoTips;
}(PhenoTips || {}));
//  ========================================================================
// End PhenoTips augmentation.


// overwrite thebehaviour of the "export/push to" button on the export preferences page:
// get the selected patients and fields and push using this javascript
['xwiki:dom:updated'].each(function(eventName) {
    document.observe(eventName, function(event) {
        if (!event.memo || !event.memo.multiPushPreferences || !event.memo.callbackOK) {
            return;
        }
        var dialog       = event.memo.multiPushPreferences.__dialog;
        var callbackOK   = event.memo.callbackOK;
        var callbackFail = event.memo.callbackFail;
        var pushButton = $('push-multiple-patients');
        pushButton && pushButton.observe('click', function(event) {
            event.stop();
            var liveMatchCounter = $('filter-match-count');
            var form = liveMatchCounter && liveMatchCounter.up('form');
            if (form) {
                // 1) get the list of selected data columns from the form
                var selectedFields = [];
                var fields = form.down('ul.field-list');
                var checkboxes = fields.select('input[name="columns"]');
                checkboxes.each(function(item) {
                    if (item.checked) {
                        selectedFields.push(item.identify().replace("columns_",""));
                    }
                });

                // 2) get the list of filtered patients from the ExportFilter page
                var url = "$xwiki.getURL('PhenoTips.ExportFilter', 'get')?list=true&" + form.serialize();
                var ajx = new Ajax.Request(url, {
                    method: 'get',
                    onSuccess: function(response) {
                        var patients = [];
                        response.responseJSON.each(function(item) {
                            patients.push(item.replace("data.",""));
                        });
                        callbackOK(patients, selectedFields);
                    },
                    onFailure: function (response) {
                        callbackFail && callbackFail();
                    }
                });
            };
            dialog.closeDialog();
        });
    });
});


document.observe("xwiki:dom:loaded", function() {
  new PhenoTips.widgets.PushPatientWidget();
});


//--- debug:

function stringifyObject(obj) {
    return _printObjectInternal(obj, 1);
}

function _printObjectInternal(o, level) {
    if (level > 10) return "...[too deep, possibly a recursive object]...";
    var output = '';
    if (typeof o == 'object')
    {
        if (Object.prototype.toString.call(o) === '[object Array]')
        {
            output = '[';
            for (var i = 0; i < o.length; i++) {
                if (i > 0) output += ', ';
                output += _printObjectInternal(o[i], level+1);
            }
            output += ']';
        }
        else
        {
            output = '{';
            var idx = 0;
            if (level == 0) output += '\n';
            for (property in o) {
                if (!o.hasOwnProperty(property)) continue;

                if (level != 0 && idx != 0 )
                    output += ', ';
                output += property + ': ' + _printObjectInternal(o[property], level+1);

                if (level == 0)
                    output += '\n';
                idx++;
            }
            output += '}';
        }
    }
    else if (typeof o == 'string') {
        output = "'" + o + "'";
    }
    else
        output = ''+o;
    return output;
}

