#macro (__filters_dateField2 $label $field_name $value $index)
{{html clean="false"}}<label>$label <span class="xHint">$services.localization.render('phenotips.databaseFilters.generalEmptyHint')</span><input class="xwiki-date" type="text" alt="#if($value)#formatdateISO($value)#end" value="$!xwiki.formatDate($value, $definedFormat)" name="${field_name}" title="${dateFormat}" /></label>{{/html}}
#end
#macro (__filters_dateRange $field_name $index $dateStart $dateEnd)
(% class="date-range" %)(((
#__filters_dateField2 ("$services.localization.render('phenotips.databaseFilters.after.label') " "${field_name}/${index}@after" $dateStart)

#__filters_dateField2 ("$services.localization.render('phenotips.databaseFilters.before.label') " "${field_name}/${index}@before" $dateEnd)
)))
#end
